queries.query:
from(bucket: "netscan")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "ping")
  |> filter(fn: (r) => r["_field"] == "success")
  |> group(columns: ["ip"])  // Process each IP's data stream individually
  |> sort(columns: ["_time"]) // Ensure records are in chronological order

  // Create a new column "consecutive_failures" that counts
  // consecutive records where _value == false.
  // The count resets to 0 when _value == true.
  |> stateCount(fn: (r) => r._value == false, column: "consecutive_failures")

  // Filter for any record that was the 3rd (or more) consecutive failure
  |> filter(fn: (r) => r.consecutive_failures >= 5)

  // Logic to get the final count
  |> group() // Merge all tables
  |> distinct(column: "ip") // Find all unique IPs that had a streak of 3+
  |> count() // Count those unique IPs

